// DEBUG: Zeigt die Struktur der empfangenen Daten
const inputData = $input.first().json;

console.log('=== DEBUG: Empfangene Daten ===');
console.log('Typ:', typeof inputData);
console.log('Ist Array?', Array.isArray(inputData));
console.log('Keys:', Object.keys(inputData));
console.log('Erste 500 Zeichen:', JSON.stringify(inputData).substring(0, 500));

// Versuche verschiedene Zugriffsmuster
let precheck = null;

if (Array.isArray(inputData)) {
  console.log('✅ inputData ist Array, nutze inputData[0]');
  precheck = inputData[0];
} else if (inputData.body && Array.isArray(inputData.body)) {
  console.log('✅ inputData.body ist Array, nutze inputData.body[0]');
  precheck = inputData.body[0];
} else if (inputData.json && Array.isArray(inputData.json)) {
  console.log('✅ inputData.json ist Array, nutze inputData.json[0]');
  precheck = inputData.json[0];
} else {
  console.log('✅ inputData ist Objekt, nutze direkt');
  precheck = inputData;
}

if (precheck) {
  console.log('Precheck-ID gefunden:', precheck.precheck_id);
  console.log('Kunde:', precheck.customer?.name);
  console.log('PV-Leistung:', precheck.project?.desired_power_kw);
} else {
  console.log('❌ Kein Precheck gefunden!');
}

return { json: { debug: 'siehe Console', inputData: inputData } };
